const initState =
{
	
	posts:
		[
			{ id: 'watches', title: 'Watches ', body:'' },
			{ id: 'Necklaces', title: 'Necklaces', body: '' },
			{ id: 'Bracelets', title: 'Bracelets', body: '' },
			{ id: 'Rings', title: 'Rings', body: '' },
			{ id: 'Earrings', title: 'Earrings', body: '' },
			{ id: 'Bags', title: 'Bags', body: '' },
			{ id: 'Shawls', title: 'Shawls', body: '' }
		]
}


const rootReducer = (state = initState, action) => {
	// console.log(action);
	if (action.type === 'DELETE_POST') {
		let newPosts = state.posts.filter(post => {
			return post.id !==action.id
		});
		return{
			...state,
			posts:newPosts
		}
	}
	return state;
}

export default rootReducer;
