import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import {connect} from 'react-redux';

class SearchResults extends Component {
	state = {
        searchText: ""
	};
	
	render() {

		const { posts } = this.props

		const filteredposts=posts.filter(
            post => {
                return post.title.toLowerCase().indexOf(
                    this.props.location.state.searchText.toLocaleLowerCase()
                ) !== -1
            }
        );


		const postList = posts.length ? (
			filteredposts.map(post => {
				const logo = post.img;
				return (
					<div className="post card" key={post.id}>
						<div className="card-content">
							<div className="blk" style={{display: 'flex', alignItems:'center'}}>
								<img src={logo} alt="" style={{display:'inline-block'}}/>
								<Link to={'/'+post.id}>
								<span className="card-title red-text">
									{post.title}
								</span>
								</Link>
							</div>
							<p>{post.body}</p>
						</div>
					</div>
				)

			})

		) : (
				<div className="center">
					No Post to show
				</div>
			);
		return (
			<div>
				<div className="home container">
					<h4 className="center"></h4>
					{postList}
				</div>
			</div>
		)
	}

}

const mapStateToProps = (state) =>
{
	return {
		posts:state.posts
	}
}


export default connect(mapStateToProps)(SearchResults);
